(function ($) {
    //扩展jQuery对象本身
    jQuery.extend({
        /**
         * 获得地址栏
         * @param name
         * @returns {*}
         */
        "getUrlParam": function (name) {
            var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)");
            var r = window.location.search.substr(1).match(reg);
            if (r != null) {
                return unescape(r[2]);
            } else {
                return null;
            }
        }

    })

})(window.jQuery);